from transaction import Player
from flask import Flask, render_template
from flask_classful import FlaskView, route
from functools import wraps
import asyncio

app = app = Flask(__name__, template_folder="templates/", static_folder="assets/")
app.config['SESSION_TYPE'] = 'memcached'
app.config['SECRET_KEY'] = 'super secret key'


def async_action(f):
    @wraps(f)
    def wrapped(*args, **kwargs):
        return asyncio.run_forever(f(*args, **kwargs))
    return wrapped


class Donablox(FlaskView):
	route_base = "/"
	User = Player("")


	@route("/")
	async def index(self):
		if not self.__awake:
			await self.__keep_awake()
			self.__awake = True

		await self.User.main()
		return render_template("index.html", donators=await self.User.get_donators())


server = Donablox()
server.register(app)

def main():
	if __name__ == '__main__':
		app.run(host='0.0.0.0', port=8080, debug=True)

main()

